import Wrapper from './components/Wrapper.js';
import './components/Addnote.css'
import './note.css'
import React, { useState } from "react";

function App() {
  const[taskList, setTaskList] = useState([{task: ""}]);

  const handleClick = () => {
    setTaskList([...taskList, {task: ""}])
    {console.log(taskList)}
  };

  const handleChange = (event,index) => {
    const {name,value} = event.target;
    const list = {...taskList};
    list[index][name] = value;
    setTaskList(list);
  };



  return (
    <div className="App">
      <Wrapper>
          {taskList.map((taskname,index) => (
            <div>
              <p class='notebox'> </p>
              {taskList.length - 1 === index && (
                <div>
                  <input type="text" id="text-box" name="message" value={taskname.task} onChange={handleChange} />
                  <button id = "submit-btn" onClick={(e) => handleClick(e,index)}>Add note</button>
                </div>
              )
              }

            </div>
          ))}


      </Wrapper>
    </div>
  );
}

export default App;
