import './App.css'
import React, { useState } from "react";
import { Textfit } from 'react-textfit';
function App() {
  const[taskList, setTaskList] = useState([{task: ""}]);

  const handleClick = () => {
    setTaskList([...taskList, {task: ""}])
    {console.log(taskList)}
  };

  const handleChange = (e, index) => {
    const {name,value} = e.target;
    const list = [...taskList];
    list[index][name] = value;
    setTaskList(list);
  };

  return (
    <div className="App">
      <div className = 'note-wrap'>
          {taskList.map((taskname,index) => (
            <div>
                <Textfit className='notebox'>{taskList[index].task}</Textfit>
            </div>
          ))}
      </div>
      {taskList.map((taskname,index) => (
        <div>
        {taskList.length - 1 === index && (
          <div className = 'Addno'>
            <input type="text" id="text-box" name="task" value={taskname.task} onChange={(e) => handleChange(e,index)} />
            <button id = "submit-btn" onClick={(e) => handleClick(e,index)}>Add note</button>
          </div>
          )
        }
        </div>
      ))
    }
    </div>
  );
}

export default App;
