          ))}
          <AddNote userTaskName = {userTask} onClick = {(e) => addNoteHandler(e)} onChange = {(e) => changeHandler(e)} >
          </AddNote>
          <p>{userTask}</p>
      </Wrapper>
    </div>
  );
}

export default App;
