import './App.css'
import React, { useState } from "react";
import { Textfit } from 'react-textfit';
import "bootstrap/dist/css/bootstrap.min.css";
// import "bootstrap/dist/js/bootstrap.bundle.min";
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card'

function App() {
  const[taskList, setTaskList] = useState([{task: ""}]);

  const handleClick = () => {
    setTaskList([...taskList, {task: ""}])
    {console.log(taskList)}
  };

  const handleChange = (e, index) => {
    const {name,value} = e.target;
    const list = [...taskList];
    list[index][name] = value;
    setTaskList(list);
  };

  return (
    <div className="App">
      <div className = 'note-wrap'>
          {taskList.map((taskname,index) => (
            <Card>
                <Textfit className='notebox' max={16}>{taskList[index].task}</Textfit>
            </Card>
          ))}
      </div>
      {taskList.map((taskname,index) => (
        <div>
        {taskList.length - 1 === index && (
          <div className = 'add-note'>
            <Form>
              <Form.Group>
                <Form.Control type="text" id="text-box" name="task" value={taskname.task} onChange={(e) => handleChange(e,index)} />
                <Button id = "submit-btn" onClick={(e) => handleClick(e,index)}>Add note</Button>
              </Form.Group>
            </Form>
          </div>
          )
        }
        </div>
      ))
    }
    </div>
  );
}

export default App;
